import React, {Component} from "react";

var cheeseCount=Number(localStorage.getItem("Cheese"));
var pepperoniCount=Number(localStorage.getItem("Pepperoni"));

function addCheese(){
    if(cheeseCount>0){
        return(
            <div>Number of Cheese Pizza: {cheeseCount}</div>
        );
    }
}

function addPepperoni(){
    return(
        <div>Number of Pepperoni Pizza: {pepperoniCount}</div>
    );
}

function createOrder(){
    localStorage.setItem("OrderInfo",addCheese());
    if(cheeseCount>0){
        orderInfo+=<div>Number of Cheese Pizza: {cheeseCount}</div>
    }
    if(pepperoniCount>0){
        orderInfo+=<div>Number of Pepperoni Pizza: {pepperoniCount}</div>
    }
    return orderInfo;
}

class OrderDetails extends Component{
    render(){
        return(
            //{addCheese}
            //addPepperoni()
            createOrder()
        );
    }
}

export default OrderDetails;
