import React, {Component} from "react";
import {
    Route, Routes,
    NavLink, HashRouter,Link
} from "react-router-dom";
import cheese from "./cheese.jpg";
import pepperoni from "./pepperoni.jpg";
import "./Menu.css";

class Menu extends Component{
    render(){            
        this.setState({
            pizza:"pizza"},
            ()=>console.log(this.state.pizza)
        );

        var orderDetail=[
            {
                name: "Cheese Pizza",
                count: 0
            },
            {
                name: "Pepperoni Pizza",
                count: 0
            }
        ];

        return(
            <div>
                <h2>Menu</h2>
                <h3>{[pizza]}</h3>
                <Link to={{
                    pathname: "../components/pages/cart/checkout",
                    state:{id:1, name:'Test',count: 0}
                }} >TestLink</Link>

                <ul className="content">                
                    <ul>
                        <li><Link to={{pathname: "../components/pages/cart/Checkout",
                                        state: orderDetail}}>
                                        <img alt="cheese" src={cheese} onClick={window.$order[0]++}/>Cheese Pizza</Link></li>
                    </ul>
                    <ul>
                        <li><NavLink to="../components/pages/cart/Checkout"><img alt="pepperoni" src={pepperoni} onClick={window.$order[0]++}/>Pepperoni Pizza</NavLink></li>
                    </ul>
                </ul>
            </div>
        );
    }
}

export default Menu;
