import React, {Component} from "react";
import {
    Route, Routes,
    NavLink, HashRouter,Link
} from "react-router-dom";
import cheese from "./cheese.jpg";
import pepperoni from "./pepperoni.jpg";
import "./Menu.css";

class Menu extends Component{
    addToOrder(){
        console.log("come handle click fun");
        this.props.history.push({pathname:"../components/pages/cart/checkout",
        state:{pizza:"Test"}});
    }
    render(){
        var pizza=0;
        return(
            <div>
                <h2>Menu</h2>
                <ul className="content">                
                    <ul>
                        <li><Link to={{pathname: "../components/pages/cart/Checkout",
                                        state: {id:1,pizza:'cheese'}}}>
                                        <img alt="cheese" src={cheese} onClick={window.$order[0]++}/>Cheese Pizza</Link></li>
                    </ul>
                    <ul>
                        <li><NavLink to="../components/pages/cart/Checkout"><img alt="pepperoni" src={pepperoni} onClick={window.$order[1]++}/>Pepperoni Pizza</NavLink></li>
                    </ul>
                </ul>
            </div>
        );
    }
}

export default Menu;
