import React, {Component, useState} from "react";
import {
    Route, Routes,
    NavLink, HashRouter,Link
} from "react-router-dom";
import cheese from "./cheese.jpg";
import pepperoni from "./pepperoni.jpg";
import "./Menu.css";

//const orderDetails=JSON.parse(localStorage.getItem("orderDetails"));
//const newOrderDetails=JSON.stringify([...orderDetails,{name:"new item"}]);
//localStorage.setItem("myItems",newOrderDetails);

const orderDetails=[
    {
        id: 0,
        pizzaType: "Cheese",
        quantity: 0
    },
    {   
        id:1,
        pizzaType: "Pepperoni",
        quantity:0
    }];

    function addToOrder(id){
        for(var i=0;i<=orderDetails.length;i++){
            if(id=orderDetails.id){
                localStorage.setItem(orderDetails.pizzaType);
            }
        }
        
    }

class Menu extends Component{

    

    render(){         
        return(
            <div>
                <h2>Menu</h2>
                <ul className="content">                
                    <ul>
                        <li><Link to={{pathname: "../components/pages/cart/Checkout"}}>
                                        <img alt="cheese" src={cheese} onClick={localStorage.setItem("CheesePizza","Cheese")}/>Cheese Pizza</Link></li>
                    </ul>
                    <ul>
                        <li><NavLink to="../components/pages/cart/Checkout"><img alt="pepperoni" src={pepperoni} onClick={addToOrder(1)}/>Pepperoni Pizza</NavLink></li>
                    </ul>
                </ul>
            </div>
        );
    }
}

export default Menu;
