import React, {Component} from "react";

class Checkout extends Component{
    constructor(order){
        super(order);
        this.state={
            value:this.order.state,
        }
    }
    render(){
        return(
            <div>
                <h2>Checkout</h2>
                <div className="order">
                    <h3>Order Details</h3>
                    <p>Cheese: {window.$order[0]}</p>
                    <p>Pepperoni: {window.$order[1]}</p>
                    <p>Total: {this.order.pizza}</p>
                </div>
            </div>
        );
    }
}

export default Checkout;
